a:1:{i:0;a:16:{s:2:"id";s:2:"52";s:5:"catid";s:2:"32";s:7:"modelid";s:1:"1";s:5:"title";s:58:"国产智能机二次出海：联想欲并购 OPPO结盟CM";s:5:"thumb";s:38:"/uploadfiles/image/shouji/201311/1.jpg";s:8:"keywords";s:23:"智能机,联想,国产";s:11:"description";s:454:"在中国市场之外的全球市场，三星、苹果们才是绝对的焦点，国产智能手机仍然只能扮演存在感薄弱的配角。品牌问题或许就是国内智能手机出海需要解决的第一大问题或者前提，在品牌问题之后，还有诸如专利、地方政策等问题需要留意。现在联想和OPPO的尝试才刚刚迈出第一步，到底这一轮中国智能手机能否出海顺利，只能靠时间来证明。";s:3:"url";s:33:"/index.php?c=content&a=show&id=52";s:9:"listorder";s:1:"0";s:6:"status";s:1:"1";s:4:"hits";s:2:"17";s:6:"sysadd";s:1:"1";s:6:"userid";s:1:"0";s:8:"username";s:5:"admin";s:9:"inputtime";s:10:"1384179711";s:10:"updatetime";s:10:"1384179711";}}